using namespace std;

int Solution(const vector<vector<int>> &input_map, int input_day)
{
    const auto mx{input_map.size() - 1}, my{input_map[0].size() - 1};
    struct P
    {
        size_t x, y, q;
    };
    vector<P> q, tq;
    for (size_t x = 0; x <= mx; ++x)
        for (size_t y = 0; y <= my; ++y)
            if (input_map[x][y] == 2)
                q.emplace_back(x, y, q.size());
    auto imap{input_map};
    vector<size_t> tot(q.size(), 0);
    for (++input_day; input_day > 0; --input_day)
    {
        for (const auto &p : q)
        {
            if (imap[p.x][p.y] == 1)
                continue;
            if (imap[p.x][p.y] >= 3)
                tot[p.q] += imap[p.x][p.y];
            imap[p.x][p.y] = 1;
            if (p.x > 0 && imap[p.x - 1][p.y] != 1)
                tq.emplace_back(p.x - 1, p.y, p.q);
            if (p.y > 0 && imap[p.x][p.y - 1] != 1)
                tq.emplace_back(p.x, p.y - 1, p.q);
            if (p.x < mx && imap[p.x + 1][p.y] != 1)
                tq.emplace_back(p.x + 1, p.y, p.q);
            if (p.y < my && imap[p.x][p.y + 1] != 1)
                tq.emplace_back(p.x, p.y + 1, p.q);
        }
        q = std::move(tq);
        tq.clear();
    }
    size_t max{0};
    for (const auto &a : tot)
        max = a > max ? a : max;
    return max;
}

class CRC32
{
private:
    static vector<uint32_t> table;

    static void InitializeTable()
    {
        const uint32_t polynomial = 0xEDB88320;
        table.resize(256);

        for (uint32_t i = 0; i < 256; i++)
        {
            uint32_t crc = i;
            for (uint32_t j = 8; j > 0; j--)
            {
                if (crc & 1)
                    crc = (crc >> 1) ^ polynomial;
                else
                    crc >>= 1;
            }
            table[i] = crc;
        }
    }

public:
    static uint32_t Compute(const string &text)
    {
        if (table.empty())
            InitializeTable();

        uint32_t crc = 0xFFFFFFFF;

        for (unsigned char c : text)
        {
            uint8_t index = static_cast<uint8_t>((crc & 0xFF) ^ c);
            crc = (crc >> 8) ^ table[index];
        }

        return ~crc;
    }
};

vector<uint32_t> CRC32::table;

void TestCase(const vector<vector<int>> &input_map, int input_day, uint32_t crc32, uint16_t passScore, uint16_t &totalScore)
{
    const int descriptionWidth = 30;
    cout << left << setw(descriptionWidth) << "Testing case";

    try
    {
        auto startTime = std::chrono::high_resolution_clock::now();
        int result = Solution(input_map, input_day);
        auto endTime = std::chrono::high_resolution_clock::now();
        std::chrono::duration<double, std::milli> elapsedTime = endTime - startTime;
        if (CRC32::Compute(to_string(result)) == crc32 && elapsedTime.count() < 50)
        {
            totalScore += passScore;
            cout << "Passed"
                 << "(" << passScore << "/" << passScore << ") "
                 << elapsedTime.count() << "ms" << endl;
        }
        else
        {
            cout << "Failed or timeout(" << elapsedTime.count() << "ms) "
                 << "(0/" << passScore << ")" << endl;
        }
    }
    catch (...)
    {
        cout << "Failed"
             << "(0/" << passScore << ")" << endl;
    }
}

